import React from "react";
import { FirstName } from "./firstName";
import { LastName } from "./lastName";
import { Email } from "./email";
import { Telephone } from "./contactNumber";
import { Device } from "./deviceType";
import { Model } from "./deviceModel";
import { SerialNumber } from "./serialNumber";
import { Date } from "./purchaseDate";
import { Problem } from "./problemDescription";
import { Attach } from "./attachFile";
import { Submit } from "./submit";

class Form extends React.Component {
  state = {
    fname: "",
    lname: "",
    email: "",
    tel: null,
    device: "",
    model: "",
    serial: "",
    purchaseDate: null,
    problem: "",
    upload: null,
    data: []
  };
  /*
  componentWillMount() {
    localStorage.getItem("fname") &&
      this.setState({ fname: JSON.parse(localStorage.getItem("fname")) });
    localStorage.getItem("lname") &&
      this.setState({ lname: JSON.parse(localStorage.getItem("lname")) });
  }

  componentDidMount() {
    if (localStorage.getItem("fname") !== null) {
      this.setState({
        data: JSON.parse(localStorage.getItem("fname"))
      });
    }
    if (localStorage.getItem("fname") === null) {
      this.setState({
        data: []
      });
    }
    document.addEventListener("submit", e => {
      e.preventDefault();
    });
  }
  */
  componentDidMount() {
    if (localStorage.getItem("data") !== null) {
      this.setState({
        data: JSON.parse(localStorage.getItem("data"))
      });
    }
    if (localStorage.getItem("data") == null) {
      let data = this.state.data;
      this.setState({
        data: data.push(this.state)
      });
    }
  }
  handleChangeInput = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  /*
  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem("fname", JSON.stringify(nextState.fname));
    localStorage.setItem("lname", JSON.stringify(nextState.lname));
  }
  */
  render() {
    const style = {
      backgroundColor: "#ffffff"
    };
    return (
      <div className="row">
        <div className="col-12">
          <div className="card-box">
            <form className="form-horizontal  p-3" style={style}>
              <p className="text-secondary font-weight-bold">WARRANTY FORM</p>
              <FirstName handleChangeInput={e => this.handleChangeInput(e)} />
              <LastName handleChangeInput={e => this.handleChangeInput(e)} />
              <Email />
              <Telephone />
              <Device />
              <Model />
              <SerialNumber />
              <Date />
              <Problem />
              <Attach />
              <Submit />
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default Form;
