import React from "react";
import { FirstName } from "./firstName";
import { LastName } from "./lastName";
import { Email } from "./email";
import { Telephone } from "./contactNumber";
import { Device } from "./deviceType";
import { Model } from "./deviceModel";
import { SerialNumber } from "./serialNumber";
import { Date } from "./purchaseDate";
import { Problem } from "./problemDescription";
import { Attach } from "./attachFile";
import { Submit } from "./submit";
import DataService from "./../dataService";

class Form extends React.Component {
  dataService = new DataService();
  state = {
    fname: "",
    lname: "",
    Email: "",
    tel: null,
    device: "",
    model: "",
    serialN: "",
    purchaseDate: null,
    problem: "",
    upload: null,
    data: []
  };
  componentDidMount() {
    if (localStorage.getItem("dataList") !== null) {
      this.setState({
        data: JSON.parse(localStorage.getItem("dataList"))
      });
    }
    if (localStorage.getItem("dataList") === null) {
      this.setState({
        data: []
      });
    }
    document.addEventListener("submit", e => {
      e.preventDefault();
    });
  }
  handleLocalStorage() {
    this.dataService.set(
      this.state.fname,
      this.state.lname,
      this.state.Email,
      this.state.tel,
      this.state.device,
      this.state.model,
      this.state.serialN,
      this.state.purchaseDate,
      this.state.problem,
      this.state.upload
    );

    this.setState({
      fname: "",
      lname: "",
      Email: "",
      tel: null,
      device: "",
      model: "",
      serialN: "",
      purchaseDate: null,
      problem: "",
      upload: null,
      data: JSON.parse(localStorage.getItem("dataList"))
    });
  }
  /*
  componentWillMount() {
    localStorage.getItem("fname") &&
      this.setState({ fname: JSON.parse(localStorage.getItem("fname")) });
    localStorage.getItem("lname") &&
      this.setState({ lname: JSON.parse(localStorage.getItem("lname")) });
    localStorage.getItem("Email") &&
      this.setState({ Email: JSON.parse(localStorage.getItem("lname")) });
  }
*/
  handleChangeInput = e => {
    e.preventDefault();
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  /*
  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem("fname", JSON.stringify(nextState.fname));
    localStorage.setItem("lname", JSON.stringify(nextState.lname));
    localStorage.setItem("Email", JSON.stringify(nextState.Email));
  }
*/
  render() {
    const style = {
      backgroundColor: "#ffffff"
    };
    return (
      <div className="row">
        <div className="col-12">
          <div className="card-box">
            <form className="form-horizontal  p-3" style={style}>
              <p className="text-secondary font-weight-bold">WARRANTY FORM</p>
              <FirstName handleChangeInput={e => this.handleChangeInput(e)} />
              <LastName handleChangeInput={e => this.handleChangeInput(e)} />
              <Email handleChangeInput={e => this.handleChangeInput(e)} />
              <Telephone />
              <Device />
              <Model />
              <SerialNumber />
              <Date />
              <Problem />
              <Attach />
              <Submit />
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default Form;
