import React from "react";
import axios from "axios";
import { FirstName } from "./firstName";
import { LastName } from "./lastName";
import { Email } from "./email";
import { Telephone } from "./contactNumber";
import { Device } from "./deviceType";
import { Model } from "./deviceModel";
import { SerialNumber } from "./serialNumber";
import { PurchaseDate } from "./purchaseDate";
import { Problem } from "./problemDescription";
import { Attach } from "./attachFile";
import { Submit } from "./submit";

class Form extends React.Component {
  componentWillMount() {
    window.addEventListener("online", () => this.checkStorage());
    window.addEventListener("load", () => this.checkStorage());
  }
  state = {
    fname: "",
    lname: "",
    email: "",
    tel: null,
    device: "",
    model: "",
    serial: null,
    purchaseDate: "",
    problem: "",
    upload: null
  };

  handleChange = e => {
    e.preventDefault();

    this.setState({
      [e.target.name]: e.target.value
    });
  };
  handleSubmit(e) {
    e.preventDefault();
    // parse form inputs into data object.
    this.getFormData();

    if (!navigator.onLine) {
      // user is offline, store data on device.
      this.storeData();
    } else {
      // user is online, send data via ajax.
      this.sendData();
    }
  }
  storeData() {
    // check if localStorage is available.
    if (typeof Storage !== "undefined") {
      const entry = this.state;
      // save data as JSON string.
      localStorage.setItem("formData", JSON.stringify(entry));
      return true;
    }
    return false;
  }
  checkStorage() {
    if (typeof Storage !== "undefined") {
      // check if we have saved data in localStorage.
      const item = localStorage.getItem(this.id);
      const entry = item && JSON.parse(item);

      if (entry) {
        // discard submissions older than one day. (optional)
        const now = new Date().getTime();
        const day = 24 * 60 * 60 * 1000;
        if (now - day > entry.time) {
          localStorage.removeItem(this.id);
          return;
        }

        // we have valid form data, try to submit it.
        this.data = entry.data;
        this.sendData();
      }
    }
  }
  sendData() {
    // send ajax request to server
    axios
      .post(this.action, this.data)
      .then(response => {
        if (response.status === 200) {
          // remove stored data on success
          localStorage.removeItem(this.id);
        }
      })
      .catch(error => {
        console.warn(error);
      });
  }
